import React, { useState, useEffect } from 'react';
import { audioManager } from '../utils/audioUtils';
import { SAMPLE_CLIPS, AudioClip, Score } from '../types/audio';
import { useVoiceRecorder } from '../hooks/useVoiceRecorder';
import { audioAnalyzer } from '../utils/audioAnalysis';

interface GameScreenProps {
  deviceId: string;
  onGameEnd: (score: number) => void;
}

export const GameScreen: React.FC<GameScreenProps> = ({ deviceId, onGameEnd }) => {
  const [round, setRound] = useState(1);
  const [countdown, setCountdown] = useState<number | null>(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [scores, setScores] = useState<Score[]>([]);
  const [currentClip, setCurrentClip] = useState<AudioClip | null>(null);
  const [loadingAudio, setLoadingAudio] = useState(true);
  const [recordingError, setRecordingError] = useState<string | null>(null);

  const { 
    startRecording, 
    stopRecording, 
    isRecording, 
    error: recorderError 
  } = useVoiceRecorder(deviceId);

  // Load audio clips when component mounts
  useEffect(() => {
    const loadAudio = async () => {
      setLoadingAudio(true);
      try {
        await audioManager.loadAllClips(SAMPLE_CLIPS);
        setLoadingAudio(false);
      } catch (error) {
        console.error('Error loading audio clips:', error);
        setLoadingAudio(false);
      }
    };

    loadAudio();
  }, []);

  const startRound = async () => {
    setRecordingError(null);
    setRound(prev => prev + 1);
    
    const unusedClips = SAMPLE_CLIPS.filter(clip => 
      !scores.some(score => score.clipId === clip.id)
    );
    const clip = unusedClips[Math.floor(Math.random() * unusedClips.length)];
    setCurrentClip(clip);
    setIsPlaying(true);
    
    try {
      await audioManager.play(clip.id);
      setIsPlaying(false);
      startCountdown();
    } catch (error) {
      console.error('Error playing audio:', error);
      setIsPlaying(false);
    }
  };

  const startCountdown = () => {
    setCountdown(3);
    const interval = setInterval(() => {
      setCountdown(prev => {
        if (prev === null || prev <= 1) {
          clearInterval(interval);
          if (prev === 1) handleStartRecording();
          return null;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const handleStartRecording = async () => {
    try {
      await startRecording();
      // Stop recording after clip duration
      setTimeout(handleStopRecording, currentClip?.duration || 5000);
    } catch (err) {
      setRecordingError('Failed to start recording');
      console.error('Recording error:', err);
    }
  };

  const handleStopRecording = async () => {
    if (!currentClip) return;
    
    try {
      const recordingBlob = await stopRecording();
      
      // Get the original audio buffer
      const originalBuffer = audioManager.getBuffer(currentClip.id);
      if (!originalBuffer) throw new Error('Original audio not found');
      
      // Get the recorded audio buffer
      const recordedBuffer = await audioAnalyzer.audioBufferFromBlob(recordingBlob);
      
      // Analyze both audio samples
      const originalFeatures = await audioAnalyzer.analyzePitch(originalBuffer);
      const recordedFeatures = await audioAnalyzer.analyzePitch(recordedBuffer);
      
      // Compare and get score
      const score = await audioAnalyzer.compareAudio(originalFeatures, recordedFeatures);
      
      setScores(prev => [...prev, {
        clipId: currentClip.id,
        score
      }]);
    } catch (err) {
      setRecordingError('Failed to analyze recording');
      console.error('Analysis error:', err);
    }
  };

  useEffect(() => {
    if (scores.length === 5) {
      const finalScore = scores.reduce((a, b) => a + b.score, 0) / scores.length;
      onGameEnd(finalScore);
    } else if (
      scores.length < 5 && 
      !isPlaying && 
      !isRecording && 
      countdown === null && 
      !loadingAudio
    ) {
      startRound();
    }
  }, [scores, isPlaying, isRecording, countdown, loadingAudio]);

  if (loadingAudio) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen p-4">
        <div className="text-2xl mb-4">Loading audio clips...</div>
        <div className="w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="text-2xl mb-8">Round {scores.length + 1}/5</div>
      
      {currentClip && isPlaying && (
        <div className="flex flex-col items-center mb-8">
          <div className="text-xl text-blue-400 mb-2">
            Listen carefully...
          </div>
          <div className="text-lg text-gray-400">
            Playing: "{currentClip.name}"
          </div>
          <div className="w-16 h-16 mt-4 border-4 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
        </div>
      )}

      {countdown !== null && (
        <div className="text-6xl mb-8 text-yellow-400 animate-pulse">
          {countdown}
        </div>
      )}

      {isRecording && (
        <div className="flex flex-col items-center mb-8">
          <div className="text-xl text-red-400 animate-pulse">
            Recording...
          </div>
          <div className="w-4 h-4 mt-4 bg-red-500 rounded-full animate-ping"></div>
        </div>
      )}

      <div className="space-y-2">
        {scores.map((score, index) => {
          const clip = SAMPLE_CLIPS.find(c => c.id === score.clipId);
          return (
            <div key={index} className="text-lg">
              Round {index + 1} - {clip?.name}: {score.score}%
            </div>
          );
        })}
      </div>

      {(recordingError || recorderError) && (
        <div className="text-red-500 mt-4">
          {recordingError || recorderError}
        </div>
      )}
    </div>
  );
};
