import React, { useEffect, useState, useRef } from 'react';

interface AudioInputBarProps {
  onDeviceSelect: (deviceId: string) => void;
}

export const AudioInputBar: React.FC<AudioInputBarProps> = ({ onDeviceSelect }) => {
  const [devices, setDevices] = useState<MediaDeviceInfo[]>([]);
  const [selectedDevice, setSelectedDevice] = useState<string>('');
  const [audioLevel, setAudioLevel] = useState<number>(0);
  const [isPlaybackEnabled, setIsPlaybackEnabled] = useState(false);
  const audioContextRef = useRef<AudioContext | null>(null);
  const streamRef = useRef<MediaStream | null>(null);

  useEffect(() => {
    const getDevices = async () => {
      try {
        await navigator.mediaDevices.getUserMedia({ audio: true });
        const devices = await navigator.mediaDevices.enumerateDevices();
        const audioDevices = devices.filter(device => device.kind === 'audioinput');
        setDevices(audioDevices);
        
        if (audioDevices.length > 0) {
          setSelectedDevice(audioDevices[0].deviceId);
          onDeviceSelect(audioDevices[0].deviceId);
        }
      } catch (error) {
        console.error('Error accessing microphone:', error);
      }
    };

    getDevices();

    return () => {
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }
    };
  }, [onDeviceSelect]);

  useEffect(() => {
    if (!selectedDevice) return;

    let animationId: number;
    
    const setupAudio = async () => {
      try {
        // Stop any existing stream
        if (streamRef.current) {
          streamRef.current.getTracks().forEach(track => track.stop());
        }
        if (audioContextRef.current) {
          audioContextRef.current.close();
        }

        // Create new audio context and stream
        const stream = await navigator.mediaDevices.getUserMedia({
          audio: { 
            deviceId: { exact: selectedDevice },
            echoCancellation: true,
            noiseSuppression: true,
            autoGainControl: true
          }
        });
        streamRef.current = stream;

        const audioContext = new AudioContext();
        audioContextRef.current = audioContext;
        const source = audioContext.createMediaStreamSource(stream);
        const analyser = audioContext.createAnalyser();
        analyser.fftSize = 1024;
        analyser.smoothingTimeConstant = 0.8;
        source.connect(analyser);

        // Connect to speakers if playback is enabled
        if (isPlaybackEnabled) {
          source.connect(audioContext.destination);
        }

        const dataArray = new Uint8Array(analyser.frequencyBinCount);
        
        const updateLevel = () => {
          analyser.getByteFrequencyData(dataArray);
          
          // Calculate RMS value for more accurate level
          let sum = 0;
          for (let i = 0; i < dataArray.length; i++) {
            sum += (dataArray[i] / 128.0) * (dataArray[i] / 128.0);
          }
          const rms = Math.sqrt(sum / dataArray.length);
          
          // Apply some scaling and smoothing
          const scaledLevel = Math.min(100, rms * 150);
          setAudioLevel(scaledLevel);
          
          animationId = requestAnimationFrame(updateLevel);
        };

        updateLevel();
      } catch (error) {
        console.error('Error setting up audio:', error);
      }
    };

    setupAudio();

    return () => {
      if (animationId) cancelAnimationFrame(animationId);
    };
  }, [selectedDevice, isPlaybackEnabled]);

  const togglePlayback = () => {
    setIsPlaybackEnabled(prev => !prev);
  };

  return (
    <div className="w-full max-w-md space-y-4">
      <select
        value={selectedDevice}
        onChange={(e) => {
          setSelectedDevice(e.target.value);
          onDeviceSelect(e.target.value);
        }}
        className="w-full p-2 bg-gray-700 text-white rounded-lg"
      >
        {devices.map(device => (
          <option key={device.deviceId} value={device.deviceId}>
            {device.label || `Microphone ${device.deviceId.slice(0, 5)}`}
          </option>
        ))}
      </select>
      
      <div className="flex items-center gap-2 mb-2">
        <input
          type="checkbox"
          id="playback"
          checked={isPlaybackEnabled}
          onChange={togglePlayback}
          className="w-4 h-4"
        />
        <label htmlFor="playback" className="text-sm">
          Enable live playback
        </label>
      </div>

      <div className="w-full h-4 bg-gray-700 rounded-full overflow-hidden">
        <div
          className="h-full bg-green-500 transition-all duration-100"
          style={{ width: `${audioLevel}%` }}
        />
      </div>
      <div className="text-xs text-gray-400 text-right">
        Level: {Math.round(audioLevel)}%
      </div>
    </div>
  );
};
